import React, { useState, useRef } from 'react';
import styled from 'styled-components';

const StyledVideoPlayer = styled.div`
  font-family: 'Helvetica Neue', Arial, sans-serif;
  background-color: #f9f9f9;
`;

const StyledForm = styled.form`
  display: flex;
  margin-bottom: 20px;
`;

const StyledInput = styled.input`
  flex-grow: 1;
  padding: 10px;
  font-size: 16px;
  border: none;
  border-radius: 4px;
  background-color: #eee;

  &::-webkit-input-placeholder {
    color: #999;
  }

  &::-moz-placeholder {
    color: #999;
  }

  &::-ms-input-placeholder {
    color: #999;
  }

  &::placeholder {
    color: #999;
  }
`;

const StyledButton = styled.button`
  margin-left: 10px;
  padding: 10px 20px;
  font-size: 16px;
  font-weight: bold;
  color: #fff;
  background-color: #007aff;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #0056b3;
  }
`;

const StyledVideo = styled.div`
  width: 100%;
  margin-bottom: 20px;
`;

const StyledList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const StyledListItem = styled.li`
  margin-bottom: 10px;
`;

const StyledLink = styled.a`
  color: #007aff;
  text-decoration: none;

  &:hover {
    text-decoration: underline;
  }
`;

const VideoPlayer = () => {
  const [videoLink, setVideoLink] = useState('');
  const [comments, setComments] = useState([]);
  const [inputComment, setInputComment] = useState('');
  const videoRef = useRef(null);

  const handleVideoLinkChange = (event) => {
    setVideoLink(event.target.value);
  };

  const handleVideoSubmit = (event) => {
    event.preventDefault();
    const videoId = extractVideoId(videoLink);
    if (videoId) {
      fetchVideoDetails(videoId);
    } else {
      alert('Invalid YouTube video link!');
    }
  };

  const fetchVideoDetails = async (videoId) => {
    const apiKey = 'AIzaSyD--hwxU-ANmrurWpZ8qjfjbgKDOIZJNig'; // Replace with your YouTube Data API key
    const response = await fetch(`https://www.googleapis.com/youtube/v3/videos?part=snippet&id=${videoId}&key=${apiKey}`);
    const data = await response.json();
    if (data.items.length > 0) {
      loadVideoPlayer(videoId);
    } else {
      alert('Failed to fetch video details!');
    }
  };

  const loadVideoPlayer = (videoId) => {
    const tag = document.createElement('script');
    tag.src = 'https://www.youtube.com/iframe_api';
    const firstScriptTag = document.getElementsByTagName('script')[0];
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

    window.onYouTubeIframeAPIReady = () => {
      new window.YT.Player(videoRef.current, {
        videoId: videoId,
        events: {
          onStateChange: handleVideoStateChange
        }
      });
    };
  };

  const handleVideoStateChange = (event) => {
    if (event.data === window.YT.PlayerState.PAUSED) {
      const currentTime = event.target.getCurrentTime();
      promptComment(currentTime);
    }
  };

  const promptComment = (timestamp) => {
    const comment = prompt('Enter your comment:');
    if (comment) {
      const newComment = {
        id: Date.now(), // Unique identifier for each comment
        timestamp: timestamp,
        text: comment
      };
      setComments((prevComments) => [...prevComments, newComment]);
    }
  };

  const handleCommentChange = (event) => {
    setInputComment(event.target.value);
  };

  const handleSubmitComment = (event) => {
    event.preventDefault();
    if (inputComment.trim() !== '') {
      const newComment = {
        id: Date.now(), // Unique identifier for each comment
        timestamp: 0,
        text: inputComment
      };
      setComments((prevComments) => [...prevComments, newComment]);
      setInputComment('');
    }
  };

  const handleDeleteComment = (commentId) => {
    setComments((prevComments) => prevComments.filter((comment) => comment.id !== commentId));
  };

  const renderComments = () => {
    return comments.map((comment) => (
      <StyledListItem key={comment.id}>
        <StyledLink href={`https://www.youtube.com/watch?v=${extractVideoId(videoLink)}&t=${comment.timestamp}`} target="_blank" rel="noopener noreferrer">
          {comment.text}
        </StyledLink>
        <StyledButton onClick={() => handleDeleteComment(comment.id)}>Delete</StyledButton>
      </StyledListItem>
    ));
  };

  const extractVideoId = (link) => {
    const videoIdMatch = link.match(/(?:[?&]v=|\/embed\/|\/[0-9a-zA-Z_-]{11})([0-9a-zA-Z_-]{11})/i);
    const unlistedIdMatch = link.match(/\/v\/([0-9a-zA-Z_-]{11})/i);
    const shortLinkMatch = link.match(/youtu\.be\/([0-9a-zA-Z_-]{11})/i);
    return videoIdMatch ? videoIdMatch[1] : unlistedIdMatch ? unlistedIdMatch[1] : shortLinkMatch ? shortLinkMatch[1] : null;
  };

  return (
    <StyledVideoPlayer>
      <StyledForm onSubmit={handleVideoSubmit}>
        <StyledInput
          type="text"
          value={videoLink}
          onChange={handleVideoLinkChange}
          placeholder="Enter YouTube video link"
        />
        <StyledButton type="submit">Load Video</StyledButton>
      </StyledForm>
      <StyledVideo ref={videoRef}></StyledVideo>
      <StyledList>{renderComments()}</StyledList>
      <StyledForm onSubmit={handleSubmitComment}>
        <StyledInput
          type="text"
          value={inputComment}
          onChange={handleCommentChange}
          placeholder="Enter your comment"
        />
        <StyledButton type="submit">Add Comment</StyledButton>
      </StyledForm>
    </StyledVideoPlayer>
  );
};

export default VideoPlayer;
