import React, { useState, useRef } from 'react';

const VideoPlayer = () => {
  const [videoLink, setVideoLink] = useState('');
  const [comments, setComments] = useState([]);
  const videoRef = useRef(null);

  const handleVideoLinkChange = (event) => {
    setVideoLink(event.target.value);
  };

  const handleVideoSubmit = (event) => {
    event.preventDefault();
    // Extract video ID from the YouTube link
    const videoId = extractVideoId(videoLink);
    // Load YouTube iframe API
    const tag = document.createElement('script');
    tag.src = 'https://www.youtube.com/iframe_api';
    const firstScriptTag = document.getElementsByTagName('script')[0];
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

    window.onYouTubeIframeAPIReady = () => {
      new window.YT.Player(videoRef.current, {
        videoId: videoId,
        events: {
          onStateChange: handleVideoStateChange
        }
      });
    };
  };

  const handleVideoStateChange = (event) => {
    // Listen for video playback events (e.g., pause, play)
    if (event.data === window.YT.PlayerState.PAUSED) {
      const currentTime = event.target.getCurrentTime();
      promptComment(currentTime);
    }
  };

  const promptComment = (timestamp) => {
    const comment = prompt('Enter your comment:');
    if (comment) {
      const newComment = {
        timestamp: timestamp,
        text: comment
      };
      setComments([...comments, newComment]);
    }
  };

  const renderComments = () => {
    return comments.map((comment, index) => (
      <li key={index}>
        <a href={`https://www.youtube.com/watch?v=${extractVideoId(videoLink)}&t=${comment.timestamp}`} target="_blank" rel="noopener noreferrer">
          {comment.text}
        </a>
      </li>
    ));
  };

  // Helper function to extract video ID from YouTube link
  const extractVideoId = (link) => {
    const videoIdMatch = link.match(/(?:[?&]v=|\/embed\/|\/[0-9a-z_-]{11})([0-9a-z_-]{11})/i);
    return videoIdMatch ? videoIdMatch[1] : null;
  };

  return (
    <div>
      <form onSubmit={handleVideoSubmit}>
        <input
          type="text"
          value={videoLink}
          onChange={handleVideoLinkChange}
          placeholder="Enter YouTube video link"
        />
        <button type="submit">Load Video</button>
      </form>
      <div ref={videoRef}></div>
      <ul>{renderComments()}</ul>
    </div>
  );
};

export default VideoPlayer;
